using Microsoft.EntityFrameworkCore;
using Size.Infrastructure.Data;
using Size.Domain.Entities;
using Size.Domain.Interfaces.Repositories;

namespace Size.Infrastructure.Repositories;

public class EmpresaRepo : IEmpresa
{
    private readonly SizeApiContext _context;

    public EmpresaRepo(SizeApiContext context) => _context = context;

    public async Task<List<Empresa>> ObterTodasAsync()
    {
        return await _context.Empresas
                             .OrderBy(empresa => empresa.Id)
                             .ToListAsync();
    }

    public async Task<Empresa> ObterPorIdAsync(Guid id)
    {
        return await _context.Empresas
                             .FindAsync(id);
    }

    public async Task<Empresa> ObterPorCnpjAsync(int cnpj)
    {
        return await _context.Empresas
                             .FirstOrDefaultAsync(e => e.Cnpj == cnpj);
    }

    public async Task<Empresa> ObterPorNomeAsync(string nome)
    {
        return await _context.Empresas.FindAsync(string nome);
    }

    public Task<Empresa> AdicionarEmpresaAsync(Empresa empresa)
    {
        throw new NotImplementedException();
    }

    public Task<Empresa> AtualizarEmpresaAsync(Empresa empresa)
    {
        throw new NotImplementedException();
    }

    public Task<Empresa> RemoverEmpresaAsync(Empresa empresa)
    {
        throw new NotImplementedException();
    }
}

