+
+namespace Size.Api;
+
+/// <summary>
+/// Teste r√°pido dos c√°lculos sem precisar do banco
+/// </summary>
+public static class TesteCalculoRapido
+{
+    public static void ExecutarTeste()
+    {
+        Console.WriteLine("=== TESTE R√ÅPIDO DE C√ÅLCULOS ===");
+        Console.WriteLine();
+
+        // Simular data atual como 15/11/2024
+        var dataAtual = new DateTime(2024, 11, 15);
+        
+        // Criar notas fiscais de teste
+        var nf1 = new NotaFiscal
+        {
+            Numero = "1",
+            Valor = 850.80m,
+            DataVencimento = new DateTime(2024, 10, 31) // 15 dias atr√°s (vencida)
+        };
+        
+        var nf10 = new NotaFiscal
+        {
+            Numero = "10",
+            Valor = 5000.00m,
+            DataVencimento = new DateTime(2024, 12, 15) // 30 dias no futuro
+        };
+        
+        var nf11 = new NotaFiscal
+        {
+            Numero = "11",
+            Valor = 7000.00m,
+            DataVencimento = new DateTime(2025, 1, 14) // 60 dias no futuro
+        };
+
+        var notasFiscais = new[] { nf1, nf10, nf11 };
+
+        foreach (var nf in notasFiscais)
+        {
+            // Simular c√°lculo com data fixa
+            var prazo = CalcularPrazoSimulado(dataAtual, nf.DataVencimento);
+            var desagio = CalcularDesagioSimulado(nf.Valor, prazo);
+            var valorLiquido = nf.Valor - desagio;
+
+            Console.WriteLine($"NF {nf.Numero}:");
+            Console.WriteLine($"  Vencimento: {nf.DataVencimento:dd/MM/yyyy}");
+            Console.WriteLine($"  Valor Bruto: R$ {nf.Valor:N2}");
+            Console.WriteLine($"  Prazo: {prazo} dias");
+            Console.WriteLine($"  Des√°gio: R$ {desagio:N2}");
+            Console.WriteLine($"  Valor L√≠quido: R$ {valorLiquido:N2}");
+            Console.WriteLine();
+        }
+
+        var totalBruto = notasFiscais.Sum(nf => nf.Valor);
+        var totalLiquido = notasFiscais.Sum(nf => nf.Valor - CalcularDesagioSimulado(nf.Valor, CalcularPrazoSimulado(dataAtual, nf.DataVencimento)));
+
+        Console.WriteLine($"TOTAIS:");
+        Console.WriteLine($"  Total Bruto: R$ {totalBruto:N2}");
+        Console.WriteLine($"  Total L√≠quido: R$ {totalLiquido:N2}");
+        
+        Console.WriteLine();
+        Console.WriteLine("=== VALORES ESPERADOS ===");
+        Console.WriteLine("NF 1: Prazo 15 dias, Des√°gio R$ 16,52, Valor L√≠quido R$ 834,28");
+        Console.WriteLine("NF 10: Prazo 30 dias, Des√°gio R$ 192,31, Valor L√≠quido R$ 4.807,69");
+        Console.WriteLine("NF 11: Prazo 60 dias, Des√°gio R$ 528,11, Valor L√≠quido R$ 6.471,89");
+        Console.WriteLine("Total L√≠quido Esperado: R$ 12.113,86");
+    }
+
+    private static int CalcularPrazoSimulado(DateTime dataAtual, DateTime dataVencimento)
+    {
+        // Para antecipa√ß√£o, calculamos quantos dias faltam at√© o vencimento
+        var diasAteVencimento = (dataVencimento.Date - dataAtual.Date).Days;
+        return Math.Max(0, diasAteVencimento);
+    }
+
+    private static decimal CalcularDesagioSimulado(decimal valor, int prazoEmDias, decimal taxaMensal = 0.04m)
+    {
+        var prazoEmMeses = prazoEmDias / 30.0m;
+        
+        if (prazoEmDias <= 0)
+            return 0;
+            
+        var fatorDesconto = (decimal)Math.Pow((double)(1 + taxaMensal), (double)prazoEmMeses);
+        var valorLiquido = valor / fatorDesconto;
+        
+        return valor - valorLiquido;
+    }
+}

