using Microsoft.EntityFrameworkCore;
using Size.Domain.Entities;

namespace Size.Application.Data;

/// <summary>
/// Contexto do banco de dados para a aplica√ß√£o Size API
/// </summary>
public class SizeApiContext : DbContext
{
    /// <summary>
    /// Construtor do contexto do banco de dados
    /// </summary>
    /// <param name="options">Op√ß√µes de configura√ß√£o do DbContext</param>
    public SizeApiContext(DbContextOptions<SizeApiContext> options) : base(options)
    {
    }

    /// <summary>
    /// Configura√ß√£o do modelo de dados
    /// </summary>
    /// <param name="modelBuilder">Construtor do modelo</param>
    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        base.OnModelCreating(modelBuilder);

        modelBuilder.Entity<Empresa>(empresa =>
                                     {
                                         empresa.HasKey(e => e.Id);

                                         empresa.Property(e => e.NomeEmpresa)
                                                .IsRequired()
                                                .HasMaxLength(1000);

                                        empresa.Property(e => e.Cnpj)
                                               .IsRequired().HasMaxLength(14);

                                        empresa.HasIndex(e => e.Cnpj).IsUnique();

                                        empresa.Property(e => e.Ramo).IsRequired().HasConversion<int>();
                                        empresa.Property(e => e.FaturamentoMensal).IsRequired().HasPrecision(2,10);

                                     });

    }
}

